class Game {
private:
    int n;
    vector<int> parent;
    vector<int> rank;
    int components;

    // Find function for union-find (with path compression)
    int find(int x) {
        if (parent[x] != x) {
            parent[x] = find(parent[x]);
        }
        return parent[x];
    }

    // Union function for union-find (with union by rank)
    bool unite(int x, int y) {
        int rootX = find(x);
        int rootY = find(y);

        if (rootX == rootY) {
            return false; // x and y are already in the same component
        }

        if (rank[rootX] > rank[rootY]) {
            parent[rootY] = rootX;
        } else if (rank[rootX] < rank[rootY]) {
            parent[rootX] = rootY;
        } else {
            parent[rootY] = rootX;
            rank[rootX]++;
        }

        components--; // Merge two components
        return true;
    }

public:
    // Initialize the game
    void initialize(int n) {
        this->n = n;
        parent.resize(n);
        rank.resize(n, 0);
        iota(parent.begin(), parent.end(), 0); // Initialize each node as its own parent
        components = n; // Initially, each city is its own component
    }

    // Answer whether to create an edge
    int hasEdge(int u, int v) {
        // If the number of components is greater than 1, add an edge to reduce components
        if (components > 1) {
            if (unite(u, v)) {
                return 1; // Add the edge
            }
        }
        // Otherwise, refuse to add the edge to delay full connectivity
        return 0;
    }
};

