    vector<string> valid_solutions;

    // Helper function to check if a solution matches the given constraints
    auto is_valid = [&](const string& solution) -> bool {
        for (int i = 0; i < n; ++i) {
            if (s[i] != '.' && s[i] != solution[i]) {
                return false; // Conflict with given constraints
            }
        }
        return true;
    };

    // Recursive function to generate all valid solutions
    function<void(int, int, string)> backtrack = [&](int block_index, int start, string current) {
        if (block_index == k) {
            // All blocks placed, fill the remaining with '_'
            current.append(n - current.size(), '_');
            if (is_valid(current)) {
                valid_solutions.push_back(current);
            }
            return;
        }

        // Try placing the current block in all valid positions
        for (int i = start; i + c[block_index] <= n; ++i) {
            // Check the block fits without conflicting with existing black or white cells
            bool can_place = true;
            for (int j = 0; j < c[block_index]; ++j) {
                if (current.size() > i + j && current[i + j] == '_') {
                    can_place = false;
                    break;
                }
            }
            if (!can_place) continue;

            // Place the block
            string next = current;
            next.append(i - current.size(), '_'); // Fill gaps with '_'
            next.append(c[block_index], 'X');    // Add the block of 'X'

            // Add a separating '_' for the next block if it's not the last one
            if (block_index < k - 1) next += '_';

            // Recurse to place the next block
            backtrack(block_index + 1, next.size(), next);
        }
    };

    // Start generating valid solutions
    backtrack(0, 0, "");

    // Determine the result by comparing all valid solutions
    string result(n, '?');
    for (int i = 0; i < n; ++i) {
        bool all_black = true, all_white = true;
        for (const auto& solution : valid_solutions) {
            if (solution[i] != 'X') all_black = false;
            if (solution[i] != '_') all_white = false;
        }
        if (all_black) result[i] = 'X';
        else if (all_white) result[i] = '_';
    }

    return result;
}

