int64_t max_weights(int N, int M, vector<int> X, vector<int> Y, vector<int> W) {
    // Step 1: Group catfish by column
    map<int, vector<pair<int, int>>> columnCatfish; // column -> [(row, weight)]
    for (int i = 0; i < M; ++i) {
        columnCatfish[X[i]].emplace_back(Y[i], W[i]);
    }

    // Step 2: Process columns to maximize weights
    map<int, int64_t> dp; // dp[column] = max weight we can achieve up to this column
    for (const auto& [column, catfish] : columnCatfish) {
        // Sort catfish in this column by row for easier processing
        vector<pair<int, int>> sortedCatfish = catfish;
        sort(sortedCatfish.begin(), sortedCatfish.end());

        // Determine the maximum weight that can be achieved for this column
        int64_t currentColumnMax = 0;
        for (const auto& [row, weight] : sortedCatfish) {
            currentColumnMax += weight; // Sum up all weights in this column
        }

        // Update dp for this column
        dp[column] = currentColumnMax;
        if (dp.find(column - 1) != dp.end()) {
            dp[column] = max(dp[column], dp[column - 1] + currentColumnMax);
        }
        if (dp.find(column - 2) != dp.end()) {
            dp[column] = max(dp[column], dp[column - 2] + currentColumnMax);
        }
    }

    // Step 3: Find the maximum weight across all columns
    int64_t maxWeight = 0;
    for (const auto& [column, weight] : dp) {
        maxWeight = max(maxWeight, weight);
    }

    return maxWeight;
}

