        sort(catfish.begin(), catfish.end());

        // DP to find the maximum weight catchable in this column
        int64_t maxCatch = 0;
        int64_t currentWeight = 0;
        int prevRow = -1;

        for (auto& [row, weight] : catfish) {
            // If there's a gap in pier coverage, reset the current weight
            if (prevRow != -1 && row != prevRow + 1) {
                currentWeight = 0;
            }

            currentWeight += weight;
            maxCatch = max(maxCatch, currentWeight);
            prevRow = row;
        }

        columnMaxCatch[col] = maxCatch;
    }

    // Step 3: Calculate total max weight
    int64_t totalMaxWeight = 0;
    for (auto& [col, maxCatch] : columnMaxCatch) {
        int64_t leftNeighbor = columnMaxCatch.count(col - 1) ? columnMaxCatch[col - 1] : 0;
        int64_t rightNeighbor = columnMaxCatch.count(col + 1) ? columnMaxCatch[col + 1] : 0;

