    auto calculatePierWeight = [&](int colIndex, int pierLength) -> int64_t {
        int col = columns[colIndex];
        int64_t weight = 0;

        for (auto &[row, w] : columnCatfish[col]) {
            if (row < pierLength) {
                weight += w;
            }
        }
        return weight;
    };

    // Process each column
    for (int i = 0; i < numColumns; i++) {
        // Option 1: Do not build a pier in this column
        dp[i] = (i > 0 ? dp[i - 1] : 0);

        // Option 2: Build a pier in this column
        int col = columns[i];
        int64_t maxWeightWithPier = 0;

        for (auto &[row, w] : columnCatfish[col]) {
            // Try different pier lengths
            for (int pierLength = 1; pierLength <= row + 1; pierLength++) {
                int64_t pierWeight = calculatePierWeight(i, pierLength);

                // Add contribution from previous column
                int64_t totalWeight = pierWeight + (i > 0 ? dp[i - 1] : 0);
                maxWeightWithPier = max(maxWeightWithPier, totalWeight);
            }
        }

        dp[i] = max(dp[i], maxWeightWithPier);
    }

    return dp[numColumns - 1];
}

