    unordered_map<int, vector<pair<int, int>>> columns; // column -> (row, weight)
    for (int i = 0; i < M; ++i) {
        columns[X[i]].emplace_back(Y[i], W[i]);
    }

    // DP array to store max weights for each column
    vector<int64_t> dp(N, 0);

    // Process each column
    for (auto& [col, fish] : columns) {
        // Sort catfish in this column by row
        sort(fish.begin(), fish.end());

        // Prefix sums for weights
        int K = fish.size();
        vector<int64_t> prefixSum(K + 1, 0);
        for (int i = 0; i < K; ++i) {
            prefixSum[i + 1] = prefixSum[i] + fish[i].second;
        }

        // Max weight calculation for this column
        int64_t maxWeight = 0;

        // Try all possible pier lengths
        for (int len = 1; len <= N; ++len) {
            int64_t currentWeight = 0;

            // Consider all possible pier placements
            for (int i = 0; i < K; ++i) {
                int row = fish[i].first;

                // Check if this catfish can be caught
                if (row >= len) {
                    // Compute weight contribution
                    int startIndex = lower_bound(fish.begin(), fish.end(), make_pair(row - len, 0)) - fish.begin();
                    int endIndex = lower_bound(fish.begin(), fish.end(), make_pair(row, 0)) - fish.begin();

                    currentWeight = prefixSum[endIndex] - prefixSum[startIndex];
                }
            }

            maxWeight = max(maxWeight, currentWeight);
        }

        dp[col] = maxWeight;
    }

    // Combine results from all columns
    int64_t result = 0;
    for (int i = 0; i < N; ++i) {
        result += dp[i];
