// Function to calculate the Euclidean distance between two points
inline int64_t euclidean_distance(int x1, int y1, int x2, int y2) {
    return abs(x1 - x2) + abs(y1 - y2);
}

int64_t min_distance(vector<int> x, vector<int> h, vector<int> l, vector<int> r, vector<int> y, int s, int g) {
    int n = x.size();
    int m = l.size();

    // Adjacency list to store the graph
    vector<vector<pair<int, int64_t>>> graph(n + m); // n buildings + m skywalks

    // Connect buildings to skywalks
    for (int j = 0; j < m; ++j) {
        int skywalk_node = n + j; // Skywalks are indexed starting from n
        int left_building = l[j];
        int right_building = r[j];
        int skywalk_y = y[j];

        // Connect skywalk endpoints to their respective buildings
        int64_t dist_left = euclidean_distance(x[left_building], h[left_building], x[left_building], skywalk_y);
        int64_t dist_right = euclidean_distance(x[right_building], h[right_building], x[right_building], skywalk_y);

        graph[left_building].emplace_back(skywalk_node, dist_left);
        graph[skywalk_node].emplace_back(left_building, dist_left);

        graph[right_building].emplace_back(skywalk_node, dist_right);
        graph[skywalk_node].emplace_back(right_building, dist_right);

        // Connect skywalk to itself for traversal
        graph[skywalk_node].emplace_back(skywalk_node, 0);
    }

    // Connect buildings directly
    for (int i = 0; i < n; ++i) {
        for (int j = i + 1; j < n; ++j) {
            if (x[i] == x[j]) {
                int64_t dist = abs(h[i] - h[j]);
                graph[i].emplace_back(j, dist);
                graph[j].emplace_back(i, dist);
            }
        }
    }

    // Dijkstra's algorithm to find the shortest path
    vector<int64_t> dist(n + m, numeric_limits<int64_t>::max());
    priority_queue<pair<int64_t, int>, vector<pair<int64_t, int>>, greater<>> pq;

    dist[s] = 0;
    pq.emplace(0, s);

    while (!pq.empty()) {
        auto [current_dist, current_node] = pq.top();
        pq.pop();

        if (current_dist > dist[current_node]) {
            continue;
        }

        for (const auto& [neighbor, weight] : graph[current_node]) {
            if (dist[current_node] + weight < dist[neighbor]) {
                dist[neighbor] = dist[current_node] + weight;
                pq.emplace(dist[neighbor], neighbor);
            }
        }
    }

    return dist[g] == numeric_limits<int64_t>::max() ? -1 : dist[g];
}

