int64 min_distance(vector<int> x, vector<int> h, vector<int> l, vector<int> r, vector<int> y, int s, int g) {
    int n = x.size();
    int m = l.size();

    // Map to store graph connections
    unordered_map<int, vector<pair<int, int64>>> graph; // node -> [(neighbor, weight)]

    // Point indexing
    int point_index = 0;
    unordered_map<pair<int, int>, int, hash<pair<int, int>>> point_to_index;
    auto get_point_index = [&](int px, int py) {
        pair<int, int> point = {px, py};
        if (point_to_index.find(point) == point_to_index.end()) {
            point_to_index[point] = point_index++;
        }
        return point_to_index[point];
    };

    // Add buildings to the graph
    vector<int> bottom_index(n), top_index(n);
    for (int i = 0; i < n; i++) {
        bottom_index[i] = get_point_index(x[i], 0);
        top_index[i] = get_point_index(x[i], h[i]);

        // Connect bottom to top of the building
        int64 height = h[i];
        graph[bottom_index[i]].emplace_back(top_index[i], height);
        graph[top_index[i]].emplace_back(bottom_index[i], height);
    }

    // Add skywalks to the graph
    for (int j = 0; j < m; j++) {
        int left_building = l[j], right_building = r[j];
        int y_level = y[j];

        int left_point = get_point_index(x[left_building], y_level);
        int right_point = get_point_index(x[right_building], y_level);

        // Connect skywalk endpoints
        int64 length = abs(x[right_building] - x[left_building]);
        graph[left_point].emplace_back(right_point, length);
        graph[right_point].emplace_back(left_point, length);

        // Connect skywalk to buildings
        graph[left_point].emplace_back(top_index[left_building], abs(h[left_building] - y_level));
        graph[top_index[left_building]].emplace_back(left_point, abs(h[left_building] - y_level));

        graph[right_point].emplace_back(top_index[right_building], abs(h[right_building] - y_level));
        graph[top_index[right_building]].emplace_back(right_point, abs(h[right_building] - y_level));
    }

    // Dijkstra's algorithm
    vector<int64> dist(point_index, INF);
    priority_queue<pair<int64, int>, vector<pair<int64, int>>, greater<>> pq;

    int start = bottom_index[s];
    int goal = bottom_index[g];
    dist[start] = 0;
    pq.emplace(0, start);

    while (!pq.empty()) {
        auto [current_dist, u] = pq.top();
        pq.pop();

        if (current_dist > dist[u]) continue;

        for (auto &[v, weight] : graph[u]) {
            if (dist[u] + weight < dist[v]) {
                dist[v] = dist[u] + weight;
                pq.emplace(dist[v], v);
            }
        }
    }

    return dist[goal] == INF ? -1 : dist[goal];
}

