int64_t min_distance(vector<int> x, vector<int> h, vector<int> l, vector<int> r, vector<int> y, int s, int g) {
    int n = x.size(); // Number of buildings
    int m = l.size(); // Number of skywalks

    // Graph representation using adjacency list
    unordered_map<int, vector<Edge>> graph;

    // Add edges between building bottoms and their tops
    for (int i = 0; i < n; ++i) {
        int bottom = i; // Bottom node index
        int top = n + i; // Top node index
        graph[bottom].push_back({top, h[i]});
        graph[top].push_back({bottom, h[i]});
    }

    // Add edges for skywalks
    for (int j = 0; j < m; ++j) {
        int left_top = n + l[j]; // Top of left building
        int right_top = n + r[j]; // Top of right building
        int skywalk_node = 2 * n + j; // Skywalk node index

        // Connect skywalk to its endpoints
        graph[skywalk_node].push_back({left_top, abs(y[j] - h[l[j]])});
        graph[left_top].push_back({skywalk_node, abs(y[j] - h[l[j]])});

        graph[skywalk_node].push_back({right_top, abs(y[j] - h[r[j]])});
        graph[right_top].push_back({skywalk_node, abs(y[j] - h[r[j]])});

        // Connect endpoints of the skywalk
        graph[skywalk_node].push_back({skywalk_node, abs(x[r[j]] - x[l[j]])});
    }

    // Dijkstra's algorithm to find the shortest path
    int start = s; // Start from the bottom of building s
    int target = g; // Target is the bottom of building g

    const int64_t INF = numeric_limits<int64_t>::max();
    unordered_map<int, int64_t> dist; // Distance from start
    for (const auto& kv : graph) {
        dist[kv.first] = INF;
    }
    dist[start] = 0;

    priority_queue<pair<int64_t, int>, vector<pair<int64_t, int>>, greater<pair<int64_t, int>>> pq;
    pq.push({0, start});

    while (!pq.empty()) {
        auto [current_dist, current_node] = pq.top();
        pq.pop();

        if (current_dist > dist[current_node]) continue;

        for (const Edge& edge : graph[current_node]) {
            int next_node = edge.node;
            int64_t weight = edge.weight;

            if (dist[current_node] + weight < dist[next_node]) {
                dist[next_node] = dist[current_node] + weight;
                pq.push({dist[next_node], next_node});
            }
        }
    }

    return dist[target] == INF ? -1 : dist[target];
}

