// Adjacency list for the directed graph
vector<vector<int>> adj;
// In-degree for topological sorting
vector<int> in_degree;
// Topological order of the plants
vector<int> topo_order;

void init(int k_, vector<int> r_) {
    k = k_;
    r = r_;
    n = r.size();

    // Initialize the adjacency list and in-degree array
    adj.assign(n, vector<int>());
    in_degree.assign(n, 0);

    // Build the graph based on the constraints from `r`
    for (int i = 0; i < n; i++) {
        for (int j = 1; j < k; j++) {
            int neighbor = (i + j) % n;
            if (r[i] > 0) {
                // Plant at `neighbor` is taller than plant `i`
                adj[i].push_back(neighbor);
                in_degree[neighbor]++;
                r[i]--;
            }
        }
    }

    // Topological sort to determine a valid order
    queue<int> q;
    for (int i = 0; i < n; i++) {
        if (in_degree[i] == 0) {
            q.push(i);
        }
    }

    topo_order.clear();
    while (!q.empty()) {
        int curr = q.front();
        q.pop();
        topo_order.push_back(curr);

        for (int neighbor : adj[curr]) {
            in_degree[neighbor]--;
            if (in_degree[neighbor] == 0) {
                q.push(neighbor);
            }
        }
    }
}

int compare_plants(int x, int y) {
    // Find positions of x and y in the topological order
    unordered_map<int, int> position;
    for (int i = 0; i < topo_order.size(); i++) {
        position[topo_order[i]] = i;
    }

    if (position[x] < position[y]) {
        return 1; // x is taller than y
    } else if (position[x] > position[y]) {
        return -1; // x is shorter than y
    } else {
        return 0; // Inconclusive (should not happen with distinct heights)
    }
}

