    for (int i = 0; i < n; ++i) {
        for (int j = 1; j < k; ++j) {
            int neighbor = (i + j) % n;
            if (r[i] > 0) {
                // Plant i has taller neighbors, so neighbor must be taller than i
                adj[i].push_back(neighbor);
                in_degree[neighbor]++;
                r[i]--;
            }
        }
    }

    // Topological sorting to assign height ranks
    queue<int> q;
    for (int i = 0; i < n; ++i) {
        if (in_degree[i] == 0) {
            q.push(i);
        }
    }

    height_rank.assign(n, 0);
    int current_rank = n - 1;

    while (!q.empty()) {
        int node = q.front();
        q.pop();
        height_rank[node] = current_rank--;
        for (int neighbor : adj[node]) {
            in_degree[neighbor]--;
            if (in_degree[neighbor] == 0) {
                q.push(neighbor);
            }
        }
    }
}

