        for (int j = 1; j < k; ++j) {
            int neighbor = (i + j) % n;
            if (r[i] == 0) {
                // If r[i] == 0, all neighbors are shorter than plant i
                definitely_taller[neighbor].insert(i);
                definitely_shorter[i].insert(neighbor);
            }
            if (r[i] == k - 1) {
                // If r[i] == k-1, all neighbors are taller than plant i
                definitely_taller[i].insert(neighbor);
                definitely_shorter[neighbor].insert(i);
            }
        }
    }

    // Propagate relationships using BFS
    for (int i = 0; i < n; ++i) {
