            }
        }

        // Propagate "definitely shorter" relationships
        for (int shorter : definitely_shorter[i]) q.push(shorter);
        while (!q.empty()) {
            int curr = q.front();
            q.pop();
            for (int shorter : definitely_shorter[curr]) {
                if (definitely_shorter[i].insert(shorter).second) {
                    q.push(shorter);
                }
            }
        }
    }
}

