// Function to initialize the problem
void init(int k_input, vector<int> r_input) {
    k = k_input;
    r = r_input;
    n = r.size();

    // Initialize the graph and in-degree array
    graph.assign(n, vector<int>());
    in_degree.assign(n, 0);

    // Build the graph based on r values
    for (int i = 0; i < n; ++i) {
        for (int j = 1; j < k; ++j) {
            int neighbor = (i + j) % n;
            if (r[i] > 0) {
                // Plant i has at least one taller plant in its next k-1 plants
                graph[neighbor].push_back(i);
                in_degree[i]++;
                r[i]--;
            }
        }
    }

    // Perform topological sort
    queue<int> q;
    for (int i = 0; i < n; ++i) {
        if (in_degree[i] == 0) {
            q.push(i);
        }
    }

    while (!q.empty()) {
        int current = q.front();
        q.pop();
        topo_order.push_back(current);

        for (int neighbor : graph[current]) {
            in_degree[neighbor]--;
            if (in_degree[neighbor] == 0) {
                q.push(neighbor);
            }
        }
    }

    // Create a position mapping for plants in topological order
    plant_position.assign(n, 0);
    for (int i = 0; i < n; ++i) {
        plant_position[topo_order[i]] = i;
    }
}

