// Function to compute the sliding window maximum
vector<int> compute_sliding_window_max(const vector<int>& H, int L, int R) {
    int n = R - L + 1;
    vector<int> max_left(n), max_right(n);
    max_left[0] = H[L];
    max_right[n - 1] = H[R];

    // Fill max_left and max_right arrays
    for (int i = 1; i < n; ++i) {
        max_left[i] = max(max_left[i - 1], H[L + i]);
        max_right[n - i - 1] = max(max_right[n - i], H[R - i]);
    }

    return {max_left, max_right};
}

int64_t calculate_cost(const vector<int>& H, int L, int R, int x, const vector<int>& max_left, const vector<int>& max_right) {
    int64_t cost = 0;

    // Add costs for mountains to the left of x
    for (int i = L; i < x; ++i) {
        cost += max_left[i - L];
    }

    // Add cost for mountain x itself
    cost += H[x];

    // Add costs for mountains to the right of x
    for (int i = x + 1; i <= R; ++i) {
        cost += max_right[i - L];
    }

    return cost;
}

vector<int64_t> minimum_costs(vector<int> H, vector<int> L, vector<int> R) {
    int Q = L.size();
    vector<int64_t> results(Q);

    for (int j = 0; j < Q; ++j) {
        int l = L[j];
        int r = R[j];

        // Compute sliding window maximums for the range [l, r]
        vector<int> max_sliding_window = compute_sliding_window_max(H, l, r);

        int64_t min_cost = INT64_MAX;

        // Try each mountain x in range [l, r] as the meeting place
        for (int x = l; x <= r; ++x) {
            int64_t current_cost = calculate_cost(H, l, r, x, max_sliding_window.first, max_sliding_window.second);
            min_cost = min(min_cost, current_cost);
        }

        results[j] = min_cost;
    }

    return results;
}

