// Function to build a Sparse Table for range maximum queries
vector<vector<int>> buildSparseTable(const vector<int>& H) {
    int N = H.size();
    int maxLog = log2(N) + 1;
    vector<vector<int>> sparseTable(N, vector<int>(maxLog));

    // Initialize table for intervals of length 1
    for (int i = 0; i < N; ++i) {
        sparseTable[i][0] = H[i];
    }

    // Build the table for larger intervals
    for (int j = 1; (1 << j) <= N; ++j) {
        for (int i = 0; i + (1 << j) - 1 < N; ++i) {
            sparseTable[i][j] = max(sparseTable[i][j - 1], sparseTable[i + (1 << (j - 1))][j - 1]);
        }
    }

    return sparseTable;
}

// Function to query the maximum in a range [L, R] using Sparse Table
int queryMax(const vector<vector<int>>& sparseTable, int L, int R) {
    int length = R - L + 1;
    int log = log2(length);
    return max(sparseTable[L][log], sparseTable[R - (1 << log) + 1][log]);
}

// Main function to calculate the minimum costs for all meetings
vector<int64_t> minimum_costs(vector<int> H, vector<int> L, vector<int> R) {
    int N = H.size();
    int Q = L.size();

    // Build Sparse Table for range maximum queries
    vector<vector<int>> sparseTable = buildSparseTable(H);

    vector<int64_t> result(Q);

    // Process each query
    for (int j = 0; j < Q; ++j) {
        int Lj = L[j], Rj = R[j];
        int64_t minCost = INT64_MAX;

        // Iterate over all possible meeting points in the range [Lj, Rj]
        for (int x = Lj; x <= Rj; ++x) {
            int64_t cost = 0;

            // Calculate cost for the left side [Lj, x]
            if (x > Lj) {
                cost += (x - Lj + 1) * queryMax(sparseTable, Lj, x);
            }

            // Calculate cost for the right side [x, Rj]
            if (x < Rj) {
                cost += (Rj - x + 1) * queryMax(sparseTable, x, Rj);
            }

            // Add the height of the meeting point itself
            cost += H[x];

            // Update the minimum cost
            minCost = min(minCost, cost);
        }

        // Store the result for this query
        result[j] = minCost;
    }

    return result;
}

