// Precompute sparse table for range maximum queries
class SparseTable {
    vector<vector<int>> st;
    vector<int> log;
public:
    SparseTable(const vector<int>& arr) {
        int n = arr.size();
        int maxLog = 32 - __builtin_clz(n); // log2(n)
        st.assign(n, vector<int>(maxLog));
        log.resize(n + 1);

        // Base case
        for (int i = 0; i < n; i++) {
            st[i][0] = arr[i];
        }

        // Compute logs
        for (int i = 2; i <= n; i++) {
            log[i] = log[i / 2] + 1;
        }

        // Build Sparse Table
        for (int j = 1; (1 << j) <= n; j++) {
            for (int i = 0; i + (1 << j) <= n; i++) {
                st[i][j] = max(st[i][j - 1], st[i + (1 << (j - 1))][j - 1]);
            }
        }
    }

    // Query the maximum in range [L, R]
    int query(int L, int R) {
        int j = log[R - L + 1];
        return max(st[L][j], st[R - (1 << j) + 1][j]);
    }
};

vector<int64_t> minimum_costs(vector<int> H, vector<int> L, vector<int> R) {
    int N = H.size();
    int Q = L.size();
    vector<int64_t> result(Q);

    // Build Sparse Table for range maximum queries
    SparseTable sparseTable(H);

    // Process each query
    for (int q = 0; q < Q; q++) {
        int l = L[q], r = R[q];
        int64_t minCost = INT64_MAX;

        // Compute prefix and suffix max arrays in range [l, r]
        vector<int64_t> prefixCost(r - l + 1, 0);
        vector<int64_t> suffixCost(r - l + 1, 0);

        // Prefix cost
        for (int x = l; x <= r; x++) {
            prefixCost[x - l] = (x == l ? 0 : prefixCost[x - l - 1]) + sparseTable.query(l, x);
        }

        // Suffix cost
        for (int x = r; x >= l; x--) {
            suffixCost[x - l] = (x == r ? 0 : suffixCost[x - l + 1]) + sparseTable.query(x, r);
        }

        // Find the minimum cost by combining prefix and suffix
        for (int x = l; x <= r; x++) {
            int64_t cost = (x == l ? 0 : prefixCost[x - l - 1]) + (x == r ? 0 : suffixCost[x - l + 1]) + H[x];
            minCost = min(minCost, cost);
        }

        result[q] = minCost;
    }

    return result;
}

