class SegmentTree {
private:
    vector<int> tree;
    int n;

    void build(const vector<int>& heights, int node, int start, int end) {
        if (start == end) {
            tree[node] = heights[start];
        } else {
            int mid = (start + end) / 2;
            build(heights, 2 * node + 1, start, mid);
            build(heights, 2 * node + 2, mid + 1, end);
            tree[node] = max(tree[2 * node + 1], tree[2 * node + 2]);
        }
    }

    int query(int node, int start, int end, int l, int r) {
        if (r < start || end < l) {
            return INT_MIN; // Out of range
        }
        if (l <= start && end <= r) {
            return tree[node]; // Fully within range
        }
        int mid = (start + end) / 2;
        int left_max = query(2 * node + 1, start, mid, l, r);
        int right_max = query(2 * node + 2, mid + 1, end, l, r);
        return max(left_max, right_max);
    }

public:
    SegmentTree(const vector<int>& heights) {
        n = heights.size();
        tree.resize(4 * n);
        build(heights, 0, 0, n - 1);
    }

    int range_max(int l, int r) {
        return query(0, 0, n - 1, l, r);
    }
};

vector<long long> minimum_costs(vector<int> H, vector<int> L, vector<int> R) {
    int N = H.size();
    int Q = L.size();

    // Initialize the segment tree for range maximum queries
    SegmentTree seg_tree(H);

    vector<long long> result(Q);

    for (int j = 0; j < Q; ++j) {
        int l = L[j], r = R[j];
        long long min_cost = LLONG_MAX;

        // Iterate over all possible meeting points in the range [l, r]
        for (int x = l; x <= r; ++x) {
            long long cost = 0;

            // Left part [l, x]
            if (l <= x) {
                cost += (long long)(x - l + 1) * seg_tree.range_max(l, x);
            }

            // Right part [x, r]
            if (x + 1 <= r) {
                cost += (long long)(r - x) * seg_tree.range_max(x + 1, r);
            }

            // Update the minimum cost
            min_cost = min(min_cost, cost);
        }

        result[j] = min_cost;
    }

    return result;
}

