            }
        }
    }

    // Validate the resulting bridge network
    for (int i = 0; i < n; i++) {
        vector<int> visited(n, 0);
        queue<pair<int, int>> q;
        q.push({i, 0}); // Start BFS from node i with distance 0
        visited[i] = 1;
        while (!q.empty()) {
            int node = q.front().first;
            int dist = q.front().second;
            q.pop();
            for (int neighbor = 0; neighbor < n; neighbor++) {
                if (b[node][neighbor] == 1 && !visited[neighbor]) {
                    visited[neighbor] = dist + 1;
                    q.push({neighbor, dist + 1});
                }
            }
        }
        for (int j = 0; j < n; j++) {
            if (visited[j] != p[i][j]) return 0; // If the number of paths does not match, return 0
        }
    }

    // Call the build function with the constructed bridge network
    build(b);
    return 1;
}

