            if (p[i][j] == 1) {
                // No direct bridge needed, already satisfies 1 path.
                continue;
            } else if (p[i][j] == 2) {
                // Add a direct bridge between i and j
                b[i][j] = 1;
                b[j][i] = 1;
            } else if (p[i][j] == 3) {
