            }
        }

        // After BFS, check if the number of paths matches `p[start][...]`
        for (int end = 0; end < n; ++end) {
            if ((start != end && visited[end] != (p[start][end] > 0)) || (start == end && visited[end] != 1)) {
                return 0; // Invalid construction
            }
        }
    }

    // Step 5: Call the build function and return success
    build(b);
