        if (human_threshold[s] <= l && human_component[s] == human_component[l]) {
            // Check if end city `e` can reach a transformation city in `[L[i], R[i]]` in the wolf subgraph
            if (wolf_threshold[e] >= r && wolf_component[e] == wolf_component[r]) {
                result[i] = 1;
            }
        }
    }

    return result;
