        // Zones: Human Zone, Wolf Zone, and Transformation Zone
        vector<bool> visitedHuman(N, false), visitedWolf(N, false), visitedTransform(N, false);

        // Compute connected components for human, wolf, and transformation zones
        vector<int> humanComponent = bfs(s, visitedHuman, l, N - 1); // Human: [L_i, N-1]
        vector<int> wolfComponent = bfs(e, visitedWolf, 0, r);       // Wolf: [0, R_i]
        vector<int> transformComponent = bfs(l, visitedTransform, l, r); // Transformation: [L_i, R_i]

        // Check if there exists a valid transformation point
