#include <vector>

std::vector<long long> mosaic(
    std::vector<int> X, 
    std::vector<int> Y,
    std::vector<int> T, 
    std::vector<int> B,
    std::vector<int> L, 
    std::vector<int> R
) {
    int N = X.size();
    int Q = T.size();

    // Step 1: Build the mosaic grid based on the rules
    std::vector<std::vector<int>> grid(N, std::vector<int>(N, 0));

    // Initialize the topmost row and the leftmost column
    for (int j = 0; j < N; ++j) {
        grid[0][j] = X[j];
    }
    for (int i = 0; i < N; ++i) {
        grid[i][0] = Y[i];
    }

    // Fill the rest of the grid
    for (int i = 1; i < N; ++i) {
        for (int j = 1; j < N; ++j) {
            // Apply the coloring rules
            if (grid[i-1][j] == 0 && grid[i][j-1] == 0) {
                grid[i][j] = 1; // Black
            } else {
                grid[i][j] = 0; // White
            }
        }
    }

    // Step 2: Precompute prefix sums for fast subrectangle queries
    std::vector<std::vector<long long>> prefixSum(N + 1, std::vector<long long>(N + 1, 0));

    for (int i = 1; i <= N; ++i) {
        for (int j = 1; j <= N; ++j) {
            prefixSum[i][j] = grid[i-1][j-1]
                            + prefixSum[i-1][j]
                            + prefixSum[i][j-1]
                            - prefixSum[i-1][j-1];
        }
    }

    // Step 3: Answer each query using the prefix sum array
    std::vector<long long> result(Q);
    for (int k = 0; k < Q; ++k) {
        int top = T[k];
        int bottom = B[k];
        int left = L[k];
        int right = R[k];

        long long sum = prefixSum[bottom + 1][right + 1]
                      - prefixSum[top][right + 1]
                      - prefixSum[bottom + 1][left]
                      + prefixSum[top][left];
        result[k] = sum;
    }

    return result;
}

