    for (int j = 0; j < N; ++j) grid[0][j] = X[j];
    for (int i = 0; i < N; ++i) grid[i][0] = Y[i];

    // Fill the rest of the grid using the coloring rule
    for (int i = 1; i < N; ++i) {
        for (int j = 1; j < N; ++j) {
            if (grid[i - 1][j] == 0 && grid[i][j - 1] == 0) {
                grid[i][j] = 1; // Black
            } else {
                grid[i][j] = 0; // White
            }
        }
    }

    // Step 2: Compute prefix sums
