    for (int i = 1; i <= N; ++i) {
        for (int j = 1; j <= N; ++j) {
            prefixSum[i][j] = grid[i - 1][j - 1]
                            + prefixSum[i - 1][j]
                            + prefixSum[i][j - 1]
                            - prefixSum[i - 1][j - 1];
