vector<long long> mosaic(
    vector<int> X, 
    vector<int> Y,
    vector<int> T, 
    vector<int> B,
    vector<int> L, 
    vector<int> R
) {
    int N = X.size();
    int Q = T.size();

    // Step 1: Construct the grid
    vector<vector<int>> grid(N, vector<int>(N, 0));
    grid[0] = X; // First row
    for (int i = 0; i < N; ++i) {
        grid[i][0] = Y[i]; // First column
    }
    for (int i = 1; i < N; ++i) {
        for (int j = 1; j < N; ++j) {
            if (grid[i-1][j] == 0 && grid[i][j-1] == 0) {
                grid[i][j] = 1; // Black
            } else {
                grid[i][j] = 0; // White
            }
        }
    }

    // Step 2: Build 2D prefix sum array
    vector<vector<long long>> prefix(N, vector<long long>(N, 0));
    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < N; ++j) {
            prefix[i][j] = grid[i][j];
            if (i > 0) prefix[i][j] += prefix[i-1][j];
            if (j > 0) prefix[i][j] += prefix[i][j-1];
            if (i > 0 && j > 0) prefix[i][j] -= prefix[i-1][j-1];
        }
    }

    // Step 3: Answer each query
    vector<long long> result(Q, 0);
    for (int k = 0; k < Q; ++k) {
        int t = T[k], b = B[k], l = L[k], r = R[k];
        long long sum = prefix[b][r];
        if (t > 0) sum -= prefix[t-1][r];
        if (l > 0) sum -= prefix[b][l-1];
        if (t > 0 && l > 0) sum += prefix[t-1][l-1];
        result[k] = sum;
    }

    return result;
}

