// Function to compute distances from a given source node
void compute_distances(int N, int source, const vector<vector<pair<int, int>>>& adj, vector<int64_t>& dist) {
    dist.assign(N, numeric_limits<int64_t>::max());
    dist[source] = 0;
    priority_queue<pair<int64_t, int>, vector<pair<int64_t, int>>, greater<>> pq;
    pq.push({0, source});

    while (!pq.empty()) {
        auto [current_dist, current_node] = pq.top();
        pq.pop();

        if (current_dist > dist[current_node]) continue;

        for (auto [neighbor, weight] : adj[current_node]) {
            int64_t new_dist = current_dist + weight;
            if (new_dist < dist[neighbor]) {
                dist[neighbor] = new_dist;
                pq.push({new_dist, neighbor});
            }
        }
    }
}

int max_score(int N, int X, int Y, int64_t K, vector<int> U, vector<int> V, vector<int> W) {
    // Build adjacency list for the graph
    vector<vector<pair<int, int>>> adj(N);
    for (int i = 0; i < N - 1; ++i) {
        adj[U[i]].emplace_back(V[i], W[i]);
        adj[V[i]].emplace_back(U[i], W[i]);
    }

    // Compute distances from X and Y to all cities
    vector<int64_t> dist_from_x, dist_from_y;
    compute_distances(N, X, adj, dist_from_x);
    compute_distances(N, Y, adj, dist_from_y);

    // Combine distances to sort cities by their reachability from X and Y
    vector<int64_t> combined_dist(N);
    for (int i = 0; i < N; ++i) {
        combined_dist[i] = min(dist_from_x[i], dist_from_y[i]);
    }
    sort(combined_dist.begin(), combined_dist.end());

    // Binary search to find maximum convenience score under budget K
    int max_score = 0;
    int64_t current_sum = 0;
    for (int i = 0; i < N; ++i) {
        if (current_sum + combined_dist[i] <= K) {
            current_sum += combined_dist[i];
            max_score += 1;  // Each city contributes to the convenience score
        } else {
            break;  // Budget exceeded
        }
    }

    return max_score;
}

