typedef long long int64;

vector<int64> dijkstra(int start, int N, const vector<vector<pair<int, int>>>& adj) {
    vector<int64> dist(N, numeric_limits<int64>::max());
    priority_queue<pair<int64, int>, vector<pair<int64, int>>, greater<pair<int64, int>>> pq;
    dist[start] = 0;
    pq.push({0, start});
    while (!pq.empty()) {
        auto [d, u] = pq.top();
        pq.pop();
        if (d > dist[u]) continue;
        for (auto [v, w] : adj[u]) {
            if (dist[u] + w < dist[v]) {
                dist[v] = dist[u] + w;
                pq.push({dist[v], v});
            }
        }
    }
    return dist;
}

int max_score(int N, int X, int Y, int64 K, vector<int> U, vector<int> V, vector<int> W) {
    // Build adjacency list
    vector<vector<pair<int, int>>> adj(N);
    for (int i = 0; i < N - 1; ++i) {
        adj[U[i]].emplace_back(V[i], W[i]);
        adj[V[i]].emplace_back(U[i], W[i]);
    }

    // Calculate shortest paths from X and Y
    vector<int64> distX = dijkstra(X, N, adj);
    vector<int64> distY = dijkstra(Y, N, adj);

    // Create a list of all cities with their distances from X and Y
    vector<pair<int64, int64>> distances;
    for (int i = 0; i < N; ++i) {
        distances.push_back({distX[i], distY[i]});
    }

    // Sort cities by their distances from X (to simplify binary search)
    sort(distances.begin(), distances.end());

    // Binary search on the maximum reachable distance
    auto canAchieve = [&](int64 maxDist) {
        int64 totalCost = 0;
        int64 scoreX = 0, scoreY = 0;

        for (const auto& [dX, dY] : distances) {
            if (dX <= maxDist) {
                totalCost += dX;
                ++scoreX;
            }
            if (dY <= maxDist) {
                totalCost += dY;
                ++scoreY;
            }
            if (totalCost > K) return false;
        }
        return true;
    };

    int64 low = 0, high = K, bestScore = 0;
    while (low <= high) {
        int64 mid = (low + high) / 2;
        if (canAchieve(mid)) {
            bestScore = mid;
            low = mid + 1;
        } else {
            high = mid - 1;
        }
    }

    return bestScore;
}

