    for (int i = 0; i < N - 1; ++i) {
        adj[U[i]].emplace_back(V[i], W[i]);
        adj[V[i]].emplace_back(U[i], W[i]);
    }

    // Helper function to calculate distances from a given source
    auto calculate_distances = [&](int src) {
        vector<int64_t> dist(N, LLONG_MAX);
        queue<int> q;
        q.push(src);
        dist[src] = 0;

        while (!q.empty()) {
            int curr = q.front();
            q.pop();
            for (auto [neighbor, weight] : adj[curr]) {
                if (dist[curr] + weight < dist[neighbor]) {
                    dist[neighbor] = dist[curr] + weight;
                    q.push(neighbor);
                }
            }
        }
        return dist;
    };

    // Step 2: Calculate distances from X and Y
    vector<int64_t> dist_from_X = calculate_distances(X);
    vector<int64_t> dist_from_Y = calculate_distances(Y);

    // Step 3: Collect all unique distances and sort them
    vector<int64_t> all_distances;
    all_distances.insert(all_distances.end(), dist_from_X.begin(), dist_from_X.end());
    all_distances.insert(all_distances.end(), dist_from_Y.begin(), dist_from_Y.end());
    sort(all_distances.begin(), all_distances.end());
    all_distances.erase(unique(all_distances.begin(), all_distances.end()), all_distances.end());

    // Step 4: Binary search on the maximum closing time
    int max_score = 0;
    for (int64_t max_closing_time : all_distances) {
        // Calculate reachable cities from X and Y within max_closing_time
        vector<int> reachable_from_X(N, 0), reachable_from_Y(N, 0);
        int64_t total_closing_time = 0;

        for (int i = 0; i < N; ++i) {
            if (dist_from_X[i] <= max_closing_time) {
                reachable_from_X[i] = 1;
                total_closing_time += dist_from_X[i];
            }
            if (dist_from_Y[i] <= max_closing_time) {
                reachable_from_Y[i] = 1;
                total_closing_time += dist_from_Y[i];
            }
        }

        // Check if total closing time exceeds K
        if (total_closing_time > K) continue;

        // Count total reachable cities
        int score = 0;
        for (int i = 0; i < N; ++i) {
            score += reachable_from_X[i] + reachable_from_Y[i];
        }

        max_score = max(max_score, score);
    }

    return max_score;
}

