int max_score(int N, int X, int Y, int64_t K, vector<int> U, vector<int> V, vector<int> W) {
    // Adjacency list representation of the graph
    vector<vector<pair<int, int>>> graph(N);
    for (int i = 0; i < U.size(); i++) {
        graph[U[i]].emplace_back(V[i], W[i]);
        graph[V[i]].emplace_back(U[i], W[i]);
    }

    // Function to calculate shortest distances from a source node using Dijkstra's algorithm
    auto calculate_distances = [&](int source) {
        vector<int64_t> distances(N, numeric_limits<int64_t>::max());
        distances[source] = 0;
        priority_queue<pair<int64_t, int>, vector<pair<int64_t, int>>, greater<>> pq;
        pq.emplace(0, source);

        while (!pq.empty()) {
            auto [current_dist, node] = pq.top();
            pq.pop();

            if (current_dist > distances[node]) continue;

            for (auto &[neighbor, weight] : graph[node]) {
                if (distances[node] + weight < distances[neighbor]) {
                    distances[neighbor] = distances[node] + weight;
                    pq.emplace(distances[neighbor], neighbor);
                }
            }
        }

        return distances;
    };

    // Calculate distances from X and Y
    vector<int64_t> dist_from_X = calculate_distances(X);
    vector<int64_t> dist_from_Y = calculate_distances(Y);

    // Combine all distances into a single vector for optimization
    vector<pair<int64_t, int>> all_distances;
    for (int i = 0; i < N; i++) {
        all_distances.emplace_back(dist_from_X[i], i);
        all_distances.emplace_back(dist_from_Y[i], i);
    }

    // Sort distances to prioritize smaller distances for allocation
    sort(all_distances.begin(), all_distances.end());

    // Allocate the closing times greedily while respecting the budget K
    vector<int64_t> closing_times(N, 0);
    int64_t used_budget = 0;
    int max_score = 0;

    for (auto &[distance, city] : all_distances) {
        if (used_budget + distance <= K) {
            int increment = (K - used_budget) / distance;
            closing_times[city] += increment;
            used_budget += increment * distance;
            max_score++;
        }
    }

    return max_score;
}

