    unordered_set<int> candidateHubs;
    vector<int> farthestDistances(N, 0);
    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < N; ++j) {
            farthestDistances[i] = max(farthestDistances[i], distances[i][j]);
        }
    }

    int R = *min_element(farthestDistances.begin(), farthestDistances.end());

    // Step 3: Check for balanced hub (subtasks 3 to 6)
    if (sub >= 3) {
        for (int hub = 0; hub < N; ++hub) {
