using namespace std;

// Function to simulate the required behavior and calculate the number of routes
void count_routes(int N, int M, int P, vector<vector<int>> R, int Q, vector<int> G) {
    // Step 1: Build the graph representation
    vector<vector<pair<int, int>>> adj(N); // adjacency list with (neighbor, trail index)
    for (int i = 0; i < M; i++) {
        int u = R[i][0], v = R[i][1];
        adj[u].emplace_back(v, i); // Add trail i from u to v
        adj[v].emplace_back(u, i); // Add trail i from v to u
    }

    // Step 2: Sort the adjacency list by trail index (beauty is implied by index order)
    for (int i = 0; i < N; i++) {
        sort(adj[i].begin(), adj[i].end(), [](const pair<int, int>& a, const pair<int, int>& b) {
            return a.second < b.second; // Sort by trail index (ascending order)
        });
    }

    // Step 3: Use dynamic programming to count routes
    // dp[k][u][last_trail]: number of ways to reach fountain u after k trails, using last_trail
    unordered_map<int, vector<unordered_map<int, long long>>> dp(2); // Use two layers for optimization
    const int MOD = 1000000007; // Modulo for large numbers

    // Initialize dp for k = 0 (only at P)
    dp[0][P][-1] = 1; // Start at P with no trails taken

    // Determine the maximum K value we need to compute
    int max_k = *max_element(G.begin(), G.end());

    // Process up to max_k steps
    for (int k = 1; k <= max_k; k++) {
        dp[k % 2].clear(); // Clear the current layer

        for (int u = 0; u < N; u++) { // Iterate over all fountains
            for (const auto& [last_trail, count] : dp[(k - 1) % 2][u]) { // Previous state
                for (const auto& [v, trail] : adj[u]) { // Explore neighbors
                    if (trail != last_trail) { // Avoid repeating the last trail if possible
                        dp[k % 2][v][trail] = (dp[k % 2][v][trail] + count) % MOD;
                    }
                }
            }
        }
    }

    // Step 4: Answer each query
    for (int i = 0; i < Q; i++) {
        int k = G[i];
        long long result = 0;

        if (k <= max_k) {
            for (const auto& [trail, count] : dp[k % 2][P]) {
                result = (result + count) % MOD; // Sum all routes ending at P with k trails
            }
        }

        // Call the answer function with the result
        answer(result);
    }
}

