void count_routes(int N, int M, int P, vector<vector<int>> R, int Q, vector<int> G) {
    // Step 1: Build the graph with trails sorted by beauty
    vector<vector<pair<int, int>>> adj(N); // adj[u] contains pairs {v, trail_id}, sorted by beauty
    for (int i = 0; i < M; ++i) {
        int u = R[i][0];
        int v = R[i][1];
        adj[u].emplace_back(v, i);
        adj[v].emplace_back(u, i);
    }

    // Sort adjacency lists by trail_id (beauty is implied by the input order of R)
    for (int i = 0; i < N; ++i) {
        sort(adj[i].begin(), adj[i].end(), [](const pair<int, int>& a, const pair<int, int>& b) {
            return a.second < b.second; // Sort by trail_id (beauty)
        });
    }

    // Step 2: Dynamic Programming (DP) to compute the number of routes
    // dp[k][u][last_trail] = number of ways to reach fountain u after exactly k trails,
    //                       where the last trail used is last_trail
    unordered_map<int, vector<vector<long long>>> dp;
    for (int k : G) {
        dp[k] = vector<vector<long long>>(N, vector<long long>(M + 1, 0));
    }

    // Initialize DP for k = 0
    for (int u = 0; u < N; ++u) {
        for (int trail_id = 0; trail_id <= M; ++trail_id) {
            if (trail_id == M) dp[0][u][trail_id] = 1; // Start without using any trail
        }
    }

    // Iterate over all values of k up to max(G)
    int max_k = *max_element(G.begin(), G.end());
    for (int k = 1; k <= max_k; ++k) {
        if (dp.find(k) == dp.end()) continue; // Skip if k is not in G

        // Iterate over all fountains
        for (int u = 0; u < N; ++u) {
            // Iterate over all possible incoming trails (including "no last trail" case M)
            for (int last_trail = 0; last_trail <= M; ++last_trail) {
                // Compute the number of routes to u using exactly k trails
                for (auto& [v, trail_id] : adj[u]) {
                    if (trail_id == last_trail) continue; // Skip the last trail used

                    dp[k][u][trail_id] += dp[k - 1][v][last_trail];
                }
            }
        }
    }

    // Step 3: Compute results for each query
    for (int k : G) {
        long long total_routes = 0;
        for (int last_trail = 0; last_trail <= M; ++last_trail) {
            total_routes += dp[k][P][last_trail];
        }
        answer(static_cast<int>(total_routes));
    }
}

