void count_routes(int N, int M, int P, vector<vector<int>> R, int Q, vector<int> G) {
    // Step 1: Build adjacency list with trails sorted by beauty
    vector<vector<pair<int, int>>> adj(N); // (neighbor, trail index)
    for (int i = 0; i < M; ++i) {
        int u = R[i][0], v = R[i][1];
        adj[u].emplace_back(v, i);
        adj[v].emplace_back(u, i);
    }

    for (int i = 0; i < N; ++i) {
        sort(adj[i].begin(), adj[i].end(), [](const pair<int, int>& a, const pair<int, int>& b) {
            return a.second < b.second; // Sort by trail index (beauty order)
        });
    }

    // Step 2: Initialize DP table
    const int MOD = 1e9 + 7;
    vector<vector<long long>> dp(2, vector<long long>(N, 0)); // We only need two layers (current and previous)
    dp[0][P] = 1; // Base case: 1 way to reach P with 0 steps

    // Step 3: Precompute answers for the maximum K in G
    int maxK = *max_element(G.begin(), G.end());
    for (int k = 1; k <= maxK; ++k) {
        int curr = k % 2, prev = 1 - curr;
        fill(dp[curr].begin(), dp[curr].end(), 0); // Reset current layer

        for (int u = 0; u < N; ++u) {
            if (dp[prev][u] == 0) continue; // No ways to reach u in k-1 steps

            int trailCount = adj[u].size();
            for (int i = 0; i < trailCount; ++i) {
                int v = adj[u][i].first; // Neighbor
                dp[curr][v] = (dp[curr][v] + dp[prev][u]) % MOD;
            }
        }
    }

    // Step 4: Answer queries
    for (int k : G) {
        answer(dp[k % 2][P]);
    }
}

