    // Step 1: Represent the graph with adjacency lists and sort edges by beauty
    vector<vector<pair<int, int>>> adj(N); // {neighbor, trail index}
    for (int i = 0; i < M; ++i) {
        int u = R[i][0];
        int v = R[i][1];
        adj[u].push_back({v, i});
        adj[v].push_back({u, i});
    }

    // Sort adjacency lists by trail index (beauty order)
    for (int i = 0; i < N; ++i) {
        sort(adj[i].begin(), adj[i].end(), [](const pair<int, int>& a, const pair<int, int>& b) {
            return a.second < b.second; // Sort by trail index (beauty)
        });
    }

    // Step 2: Dynamic programming to count routes
    // dp[k][u][last_trail]: Number of ways to reach fountain `u` in `k` steps, coming via `last_trail`
    unordered_map<int, vector<unordered_map<int, long long>>> dp(Q);

    // Base case: At step 0, only `P` is reachable, with no trail used
    for (int q = 0; q < Q; ++q) {
        int K = G[q];
        dp[q].resize(K + 1);
        dp[q][0][P] = 1; // Start at fountain P
    }

    // For each query, compute paths using dynamic programming
    for (int q = 0; q < Q; ++q) {
        int K = G[q];
        // Iterate over steps from 1 to K
        for (int k = 1; k <= K; ++k) {
            // Iterate over all fountains
            for (int u = 0; u < N; ++u) {
                // If there are no ways to reach `u` in `k-1` steps, skip
                if (dp[q][k - 1].find(u) == dp[q][k - 1].end()) continue;

                // Iterate over all trails from `u`
                for (int i = 0; i < (int)adj[u].size(); ++i) {
                    int v = adj[u][i].first;
                    int trail = adj[u][i].second;

                    // Consider the most beautiful trail and avoid reusing the same trail consecutively
                    if (i == 0 || (i > 0 && trail != adj[u][i - 1].second)) {
                        dp[q][k][v] += dp[q][k - 1][u];
                    }
                }
            }
        }
    }

    // Step 3: Compute results for each query
    for (int q = 0; q < Q; ++q) {
        int K = G[q];
        long long result = 0;

        // Sum all paths that reach any fountain after exactly K steps
        for (int u = 0; u < N; ++u) {
            result += dp[q][K][u];
        }

        // Report the result for this query
        answer(result % 1000000007); // Modulo 10^9+7 to prevent overflow
    }
}

