vector<int> construct_spanning_tree(int n, const vector<int>& u, const vector<int>& v) {
    vector<vector<int>> adj(n);
    for (int i = 0; i < u.size(); ++i) {
        adj[u[i]].push_back(i);
        adj[v[i]].push_back(i);
    }

    vector<int> visited(n, 0), spanning_tree;
    queue<int> q;
    visited[0] = 1;
    q.push(0);

    while (!q.empty() && spanning_tree.size() < n - 1) {
        int city = q.front();
        q.pop();

        for (int road : adj[city]) {
            int next_city = (u[road] == city) ? v[road] : u[road];
            if (!visited[next_city]) {
                visited[next_city] = 1;
                q.push(next_city);
                spanning_tree.push_back(road);
                if (spanning_tree.size() == n - 1) break;
            }
        }
    }

    return spanning_tree;
}

