vector<int> find_roads(int n, vector<int> u, vector<int> v) {
    int m = u.size();
    vector<int> all_roads(m);
    iota(all_roads.begin(), all_roads.end(), 0); // Label roads from 0 to m-1

    // Initial golden set (spanning tree)
    vector<int> golden_set = construct_spanning_tree(n, u, v);

    // Binary search for the royal roads
    vector<int> royal_roads;
    vector<int> remaining_roads = golden_set;

    while (!remaining_roads.empty()) {
        int mid = remaining_roads.size() / 2;
        vector<int> left_set(remaining_roads.begin(), remaining_roads.begin() + mid);
        vector<int> right_set(remaining_roads.begin() + mid, remaining_roads.end());

        // Check left set
        int left_count = count_common_roads(left_set);
        if (left_count > 0) {
            if (left_set.size() == 1) {
                royal_roads.push_back(left_set[0]);
            } else {
                remaining_roads = left_set;
                continue;
            }
        }

        // Check right set
        int right_count = count_common_roads(right_set);
        if (right_count > 0) {
            if (right_set.size() == 1) {
                royal_roads.push_back(right_set[0]);
            } else {
                remaining_roads = right_set;
                continue;
            }
        }

        remaining_roads.clear(); // If neither set has royal roads, break
    }

    return royal_roads;
}

