        iota(parent.begin(), parent.end(), 0); // Disjoint-set initialization

        // Find operation for disjoint set
        auto find = [&](int x) {
            while (x != parent[x]) x = parent[x];
            return x;
        };

        // Union operation for disjoint set
        auto unite = [&](int x, int y) {
            x = find(x);
            y = find(y);
            if (x != y) parent[x] = y;
        };

        vector<int> golden_set;
        for (int edge : edges) {
            int city1 = u[edge];
            int city2 = v[edge];
            if (find(city1) != find(city2)) {
                golden_set.push_back(edge);
                unite(city1, city2);
                if (golden_set.size() == n - 1) break; // Stop once we have n-1 edges
            }
        }
        return golden_set;
    };

    // Binary search to determine the royal roads
    vector<int> all_edges(m);
    iota(all_edges.begin(), all_edges.end(), 0); // Initialize with all edge indices

    for (int i = 0; i < m; ++i) {
        // Exclude the i-th edge and construct a golden set
        vector<int> edges = all_edges;
        edges.erase(edges.begin() + i); // Remove the current edge

        // Build a golden set without the i-th edge
        vector<int> golden_set = build_golden_set(edges);

        // Query the golden set
        int count = count_common_roads(golden_set);

        // If count is less than n-1, the i-th edge must be a royal road
        if (count < n - 1) {
