// Helper function to build and return a Minimum Spanning Tree (MST)
vector<int> build_mst(int n, const vector<int>& u, const vector<int>& v) {
    int m = u.size();
    vector<pair<int, int>> edges; // Pair of (road index, road weight)
    for (int i = 0; i < m; ++i) {
        edges.emplace_back(i, 1); // Use weight 1 for all edges (arbitrary, as weights are not given)
    }

    // Sort edges by weight (all weights are the same here, so order doesn't matter)
    sort(edges.begin(), edges.end(), [](auto& a, auto& b) {
        return a.second < b.second;
    });

    // Union-Find (Disjoint Set Union) for Kruskal's MST
    vector<int> parent(n), rank(n, 0);
    iota(parent.begin(), parent.end(), 0); // Initialize parent array

    function<int(int)> find = [&](int x) {
        if (parent[x] != x) parent[x] = find(parent[x]);
        return parent[x];
    };

    auto unite = [&](int x, int y) {
        x = find(x);
        y = find(y);
        if (x != y) {
            if (rank[x] < rank[y]) swap(x, y);
            parent[y] = x;
            if (rank[x] == rank[y]) ++rank[x];
        }
    };

    // Build the MST
    vector<int> mst;
    for (auto& edge : edges) {
        int idx = edge.first;
        if (find(u[idx]) != find(v[idx])) {
            mst.push_back(idx);
            unite(u[idx], v[idx]);
            if (mst.size() == n - 1) break; // Stop when we have n-1 edges
        }
    }

    return mst;
}

// Main function to find the royal roads
vector<int> find_roads(int n, vector<int> u, vector<int> v) {
    int m = u.size();

    // Step 1: Build an initial MST
    vector<int> mst = build_mst(n, u, v);

    // Step 2: Use binary search to identify royal roads
    vector<int> royal_roads; // To store the royal roads
    vector<bool> is_royal(m, false); // To track which roads are royal
    for (int road : mst) is_royal[road] = true; // Initially mark MST edges as potential royal roads

    for (int i = 0; i < mst.size(); ++i) {
        int road_to_test = mst[i];

        // Remove the current road from the MST
        vector<int> test_set = mst;
        test_set.erase(test_set.begin() + i);

        // Try replacing it with any other edge not currently in the MST
        for (int j = 0; j < m; ++j) {
            if (!is_royal[j]) {
                test_set.push_back(j); // Add the new edge
                if (test_set.size() == n - 1) {
                    // Query Simurgh
                    int common_count = count_common_roads(test_set);
                    if (common_count > royal_roads.size()) {
                        // This means the road `j` is royal
                        royal_roads.push_back(j);
                        is_royal[j] = true;
                        break;
                    }
                }
                test_set.pop_back(); // Remove the edge after testing
            }
        }
    }

    return royal_roads;
}

