    // Function to find the next connected node to extend the trip
    auto find_next = [&](int current) -> int {
        for (int i = 0; i < N; ++i) {
            if (!visited[i]) {
                if (query({current}, {i})) {
                    return i;
                }
            }
        }
        return -1;  // No unvisited connected node found
    };

    // Extend the trip forward
    while (true) {
        int next = find_next(trip.back());
        if (next == -1) break;  // No more nodes to extend
        trip.push_back(next);
        visited[next] = true;
    }

    // Extend the trip backward
    while (true) {
        int next = find_next(trip.front());
        if (next == -1) break;  // No more nodes to extend
        trip.insert(trip.begin(), next);
        visited[next] = true;
    }

    return trip;
}

