        vector<int> parent(N, -1);
        queue<int> q;

        dist[start] = 0;
        q.push(start);

        int furthest_node = start;
        while (!q.empty()) {
            int node = q.front();
            q.pop();

            for (int neighbor : adj[node]) {
                if (dist[neighbor] == -1) {
                    dist[neighbor] = dist[node] + 1;
                    parent[neighbor] = node;
                    q.push(neighbor);
                    if (dist[neighbor] > dist[furthest_node]) {
                        furthest_node = neighbor;
                    }
                }
            }
        }

        // Trace back the longest path from the furthest node
        vector<int> path;
        for (int node = furthest_node; node != -1; node = parent[node]) {
            path.push_back(node);
        }
        reverse(path.begin(), path.end());
        return path;
    };

    // Find the longest path starting from any node
    vector<int> longest_path = bfs_longest_path(0);

    // Return the longest path found
    return longest_path;
}

