// Helper function to check if a rectangular set is beautiful
bool is_beautiful(int r1, int r2, int c1, int c2) {
    int expected_min = r1 * W + c1; // Minimum contestant number
    int expected_max = r2 * W + c2; // Maximum contestant number
    int k = (r2 - r1 + 1) * (c2 - c1 + 1);

    // Check if the range [expected_min, expected_max] matches the contestant numbers
    set<int> contestants;
    for (int r = r1; r <= r2; ++r) {
        for (int c = c1; c <= c2; ++c) {
            int seat_index = flatten(r, c);
            contestants.insert(seat_to_contestant[seat_index]);
        }
    }

    if (contestants.size() != k) return false;

    int current = expected_min;
    for (int contestant : contestants) {
        if (contestant != current) return false;
        ++current;
    }

    return true;
}

