vector<set<int>> cols; // cols[c] = set of contestant indices in column c

// Helper function to check if a set of contestants is beautiful
bool is_beautiful(const set<int>& s) {
    if (s.empty()) return true;
    auto it = s.begin();
    int first = *it, last = *it, count = 1;
    ++it;
    for (; it != s.end(); ++it) {
        if (*it != last + 1) return false;
        last = *it;
        ++count;
    }
    return last - first + 1 == count;
}

// Helper function to calculate beauty of the chart
int calculate_beauty() {
    int beauty = 0;
    for (const auto& row : rows) {
        beauty += is_beautiful(row);
    }
    for (const auto& col : cols) {
        beauty += is_beautiful(col);
    }
    return beauty;
}

