int H, W;
vector<pair<int, int>> pos; // Position of each contestant
vector<vector<int>> grid;  // Grid mapping (row, col) to contestant
vector<int> parent, rank, size; // Union-Find data structures

// Helper functions for Union-Find
int find(int x) {
    if (parent[x] != x) {
        parent[x] = find(parent[x]);
    }
    return parent[x];
}

void unite(int x, int y) {
    int rootX = find(x);
    int rootY = find(y);
    if (rootX != rootY) {
        if (rank[rootX] < rank[rootY]) {
            swap(rootX, rootY);
        }
        parent[rootY] = rootX;
        size[rootX] += size[rootY];
        if (rank[rootX] == rank[rootY]) {
            rank[rootX]++;
        }
    }
}

// Initialize the seating chart
void give_initial_chart(int h, int w, vector<int> R, vector<int> C) {
    H = h;
    W = w;

    pos.resize(H * W);
    grid.assign(H, vector<int>(W, -1));
    parent.resize(H * W);
    rank.resize(H * W, 0);
    size.resize(H * W, 1);

    // Initialize positions and grid
    for (int i = 0; i < H * W; i++) {
        pos[i] = {R[i], C[i]};
        grid[R[i]][C[i]] = i;
        parent[i] = i; // Each contestant is its own parent initially
    }

    // Union-Find setup for initial chart
    for (int r = 0; r < H; r++) {
        for (int c = 0; c < W; c++) {
            int contestant = grid[r][c];
            if (r > 0 && grid[r - 1][c] != -1 && grid[r - 1][c] + 1 == contestant) {
                unite(contestant, grid[r - 1][c]);
            }
            if (c > 0 && grid[r][c - 1] != -1 && grid[r][c - 1] + 1 == contestant) {
                unite(contestant, grid[r][c - 1]);
            }
        }
    }
}

// Calculate the beauty of the current chart
int calculate_beauty() {
    unordered_map<int, int> beautifulSets;
    for (int i = 0; i < H * W; i++) {
        int root = find(i);
        if (size[root] == root + 1) { // Check if the set is beautiful
            beautifulSets[root] = 1;
        }
    }
    return beautifulSets.size();
}

// Handle a swap operation and return the new beauty
int swap_seats(int a, int b) {
    // Swap positions in the grid
    auto [ra, ca] = pos[a];
    auto [rb, cb] = pos[b];
    grid[ra][ca] = b;
    grid[rb][cb] = a;
    pos[a] = {rb, cb};
    pos[b] = {ra, ca};

    // Recalculate union-find connections in the affected rows/columns
    vector<int> affected = {a, b};
    for (int contestant : affected) {
        auto [r, c] = pos[contestant];
        parent[contestant] = contestant;
        size[contestant] = 1;
        if (r > 0 && grid[r - 1][c] != -1 && grid[r - 1][c] + 1 == contestant) {
            unite(contestant, grid[r - 1][c]);
        }
        if (r < H - 1 && grid[r + 1][c] != -1 && grid[r + 1][c] - 1 == contestant) {
            unite(contestant, grid[r + 1][c]);
        }
        if (c > 0 && grid[r][c - 1] != -1 && grid[r][c - 1] + 1 == contestant) {
            unite(contestant, grid[r][c - 1]);
        }
        if (c < W - 1 && grid[r][c + 1] != -1 && grid[r][c + 1] - 1 == contestant) {
            unite(contestant, grid[r][c + 1]);
        }
    }

    // Return the new beauty
    return calculate_beauty();
}

