int64_t find_maximum(int k, vector<vector<int>> x) {
    int n = x.size();    // Number of colors
    int m = x[0].size(); // Tickets per color

    // Result allocation matrix
    vector<vector<int>> s(n, vector<int>(m, -1));

    // Priority queue to store all tickets with their values and color indices
    vector<pair<int, pair<int, int>>> tickets; // {value, {color, index}}

    // Collect all tickets into a single list
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            tickets.push_back({x[i][j], {i, j}});
        }
    }

    // Sort tickets in descending order by value
    sort(tickets.rbegin(), tickets.rend());

    // Greedy allocation of tickets to rounds
    vector<vector<int>> rounds(k); // Each round stores selected ticket values
    vector<int> color_used(n, 0);  // Track how many tickets are used per color

    int round = 0;
    for (const auto& ticket : tickets) {
        int value = ticket.first;
        int color = ticket.second.first;
        int index = ticket.second.second;

        // If this color still has tickets to allocate
        if (color_used[color] < k) {
            s[color][index] = round; // Allocate this ticket to the current round
            rounds[round].push_back(value);
            color_used[color]++;

            // Move to the next round in a cyclic manner
            round = (round + 1) % k;
        }
    }

    // Calculate the total maximum prize value
    int64_t total_prize = 0;
    for (const auto& round_values : rounds) {
        // Sort the values in the round to find the optimal median
        vector<int> sorted_values = round_values;
        sort(sorted_values.begin(), sorted_values.end());
        int median = sorted_values[sorted_values.size() / 2];

        // Calculate the sum of absolute differences for this round
        int64_t round_prize = 0;
        for (int value : sorted_values) {
            round_prize += abs(value - median);
        }
        total_prize += round_prize;
    }

    // Call the grader function with the allocation matrix
    allocate_tickets(s);

    // Return the total maximum prize value
    return total_prize;
}

