    // Total prize value
    int64_t total_prize_value = 0;

    // Allocate tickets round by round
    while (!pq.empty()) {
        auto top = pq.top();
        pq.pop();

        int value = top.first;
        int color = top.second.first;
        int index = top.second.second;

        // Find a round that still needs tickets
        for (int r = 0; r < k; ++r) {
            if (round_count[r] < tickets_per_round) {
                // Allocate this ticket to this round
                s[color][index] = r;
                round_count[r]++;
                total_prize_value += value;
                break;
            }
        }
    }

    // Call the grader's function to allocate tickets
    allocate_tickets(s);

    // Return the total prize value
    return total_prize_value;
}

