int64_t find_maximum(int k, vector<vector<int>> x) {
    int n = x.size(); // Number of colors
    int m = x[0].size(); // Tickets per color

    // Result allocation matrix
    vector<vector<int>> s(n, vector<int>(m, -1));

    // Prepare a list of top-k values for each color
    vector<vector<int>> top_k_values(n);
    for (int i = 0; i < n; ++i) {
        top_k_values[i].assign(x[i].end() - k, x[i].end());
        reverse(top_k_values[i].begin(), top_k_values[i].end());
    }

    // Priority queue to maximize the distribution of high values
    vector<vector<pair<int, int>>> rounds(k); // Each round contains (value, color index)
    for (int i = 0; i < k; ++i) {
        for (int j = 0; j < n; ++j) {
            rounds[i].push_back({top_k_values[j][i], j});
        }
    }

    // Sort each round in descending order by value
    for (int i = 0; i < k; ++i) {
        sort(rounds[i].rbegin(), rounds[i].rend());
    }

    // Allocate tickets to maximize the sum of medians
    int64_t total_prize_value = 0;
    vector<int> ticket_indices(n, m - k); // Start allocating from the last k tickets
    for (int r = 0; r < k; ++r) {
        vector<int> round_values;
        for (auto &[value, color] : rounds[r]) {
            round_values.push_back(value);
            s[color][ticket_indices[color]++] = r;
        }

        // Calculate the median of the current round
        nth_element(round_values.begin(), round_values.begin() + n / 2, round_values.end());
        int median = round_values[n / 2];

        // Calculate the prize value for this round
        int64_t round_prize_value = 0;
        for (int value : round_values) {
            round_prize_value += abs(value - median);
        }

        total_prize_value += round_prize_value;
    }

    // Call the grader function
    allocate_tickets(s);

    return total_prize_value;
}

