vector<long long> segmentTree;

// Helper function to compute modular multiplication
long long modMult(long long a, long long b) {
    return (a * b) % MOD;
}

// Helper function to build the segment tree
void buildSegmentTree(int node, int start, int end) {
    if (start == end) {
        // Leaf node, store the profit
        segmentTree[node] = modMult(prefixProduct[start], Y[start]);
    } else {
        int mid = (start + end) / 2;
        buildSegmentTree(2 * node + 1, start, mid);
        buildSegmentTree(2 * node + 2, mid + 1, end);
        segmentTree[node] = max(segmentTree[2 * node + 1], segmentTree[2 * node + 2]);
    }
}

// Helper function to update the segment tree
void updateSegmentTree(int node, int start, int end, int idx) {
    if (start == end) {
        // Leaf node, update the profit
        segmentTree[node] = modMult(prefixProduct[start], Y[start]);
    } else {
        int mid = (start + end) / 2;
        if (idx <= mid) {
            updateSegmentTree(2 * node + 1, start, mid, idx);
        } else {
            updateSegmentTree(2 * node + 2, mid + 1, end, idx);
        }
        segmentTree[node] = max(segmentTree[2 * node + 1], segmentTree[2 * node + 2]);
    }
}

// Function to initialize the solution
int init(int n, int x[], int y[]) {
    N = n;
    X = vector<int>(x, x + N);
    Y = vector<int>(y, y + N);

    // Compute prefix products
    prefixProduct = vector<long long>(N);
    prefixProduct[0] = X[0];
    for (int i = 1; i < N; ++i) {
        prefixProduct[i] = modMult(prefixProduct[i - 1], X[i]);
    }

    // Build the segment tree
    int treeSize = 4 * N;
    segmentTree = vector<long long>(treeSize, 0);
    buildSegmentTree(0, 0, N - 1);

    // Return the maximum profit
    return segmentTree[0];
}

// Function to handle updates to X
int updateX(int pos, int val) {
    // Update the X array
    X[pos] = val;

    // Update prefix products
    prefixProduct[pos] = X[pos];
    if (pos > 0) {
        prefixProduct[pos] = modMult(prefixProduct[pos - 1], prefixProduct[pos]);
    }
    for (int i = pos + 1; i < N; ++i) {
        prefixProduct[i] = modMult(prefixProduct[i - 1], X[i]);
    }

    // Update the segment tree
    for (int i = pos; i < N; ++i) {
        updateSegmentTree(0, 0, N - 1, i);
    }

    // Return the maximum profit
    return segmentTree[0];
}

// Function to handle updates to Y
int updateY(int pos, int val) {
    // Update the Y array
    Y[pos] = val;

    // Update the segment tree
    updateSegmentTree(0, 0, N - 1, pos);

    // Return the maximum profit
    return segmentTree[0];
}

