void find_pair(int N, vector<int> U, vector<int> V, int A, int B) {
    int M = U.size();

    // Step 1: Initialize all traffic conditions to light (w[i] = 0 for all i)
    vector<int> w(M, 0);
    int64_t light_cost = ask(w); // Query the cost with all light tolls

    // Step 2: Initialize all traffic conditions to heavy (w[i] = 1 for all i)
    fill(w.begin(), w.end(), 1);
    int64_t heavy_cost = ask(w); // Query the cost with all heavy tolls

    // The shortest path in terms of light tolls would be `light_cost / A`
    // The shortest path in terms of heavy tolls would be `heavy_cost / B`
    int light_path_length = light_cost / A;
    int heavy_path_length = heavy_cost / B;

    // Step 3: Identify the endpoint of the shortest path in terms of light tolls
    // We will set one edge to heavy at a time to find which edge lies on the path
    vector<int> endpoints;
    w.assign(M, 0); // Reset to all light
    for (int i = 0; i < M; i++) {
        w[i] = 1; // Temporarily set edge `i` to heavy
        int64_t cost = ask(w);
        if (cost == light_cost + (B - A)) { // This edge contributes to the shortest path
            endpoints.push_back(U[i]);
            endpoints.push_back(V[i]);
        }
        w[i] = 0; // Reset edge `i` back to light
        if (endpoints.size() > 2) break; // We only need two endpoints
    }

    // Step 4: Deduce the pair S and T
    // The two endpoints of the light toll shortest path are likely S and T
    int s = endpoints[0];
    int t = endpoints[1];

    // Verify the pair using the heavy toll shortest path
    w.assign(M, 1); // Set all edges to heavy
    for (int i = 0; i < M; i++) {
        if ((U[i] == s && V[i] == t) || (U[i] == t && V[i] == s)) {
            w[i] = 0; // Temporarily set edge `i` to light
            break;
        }
    }
    int64_t verify_cost = ask(w);
    if (verify_cost != heavy_cost) {
        swap(s, t); // If the costs don't match, swap S and T
    }

    // Step 5: Report the answer
    answer(s, t);
}

